WITH temp_data AS (
  SELECT '020900000003002SFG9312205S3SECRETARIA DE EDUCACION PUBLICA DE BAJA 6091063000330001120240620250205202502072590724403900000000000AAAM720714QA2AAAM720714HBSLNN00ALVAREZ                                 ANGULO                                  MANUEL VENTURA                          2010161804060000000000000000000000000003236000000005137000000009910000000000000000000000021000000000033000000000064000000000000000000008090000000000000000000000052100000000000000000000002605461900000000000000000000000016747100000310501570102506000000000000000000000000000000000000000000000000                                      00000000000' AS FTC_LINEA
  FROM dual
)    
SELECT CONCAT(
    SUBSTR((FTC_LINEA :: STRING), 1, 442),
    CASE 
        WHEN SUBSTR((FTC_LINEA :: STRING), 443, 1) = 1
        THEN '2' 
        ELSE '0' 
    END,
    SUBSTR((FTC_LINEA :: STRING), 444, 177)
) AS resultado
FROM temp_data